CREATE OR REPLACE PROCEDURE Cadastra_Contador( IN C_nome VARCHAR(100) CHARSET utf8, IN C_cpf VARCHAR(11) CHARSET utf8, IN C_crc VARCHAR(15) CHARSET utf8, IN C_cnpj VARCHAR(14) CHARSET utf8, IN C_cep VARCHAR(8) CHARSET utf8, IN C_logradouro VARCHAR(60) CHARSET utf8, IN C_numero VARCHAR(10) CHARSET utf8, IN C_complemento VARCHAR(60) CHARSET utf8, IN C_bairro VARCHAR(60) CHARSET utf8, IN C_fone VARCHAR(11) CHARSET utf8, IN C_fax VARCHAR(11) CHARSET utf8, IN C_email VARCHAR(255) CHARSET utf8, IN C_cod_mun VARCHAR(7) CHARSET utf8 ) BEGIN IF ( (SELECT COUNT(id) FROM tb_contabilista WHERE cpf = @cpf OR cnpj = @cnpj) = 0 ) THEN INSERT INTO tb_contabilista (nome, cpf, crc, cnpj, cep, logradouro, numero, complemento, bairro, fone, fax, email, cod_mun) VALUES ( C_nome, C_cpf, C_crc, C_cnpj, C_cep, C_logradouro, C_numero, C_complemento, C_bairro, C_fone, C_fax, C_email, C_cod_mun ); SET @idContabilista=(SELECT LAST_INSERT_ID()); ELSE SET @idContabilista=(SELECT id FROM tb_contabilista WHERE cpf = C_cpf OR cnpj = C_cnpj); END IF; END $$ CREATE OR REPLACE PROCEDURE Cadastra_Spedfiscal( INOUT S_cod_ver INT, IN S_cod_fin INT, IN S_dt_ini INT, IN S_dt_fin INT, IN S_cnpj_fornecedor VARCHAR(17) CHARSET utf8, IN S_id_contabilista INT, IN S_ind_perfil ENUM("A", "B", "C") ) BEGIN IF ( (SELECT COUNT(id) FROM tb_spedfiscal WHERE dt_ini = S_dt_ini AND dt_fin = S_dt_fin AND cnpj_fornecedor = S_cnpj_fornecedor) = 0 ) THEN INSERT INTO tb_spedfiscal (cnpj_fornecedor, id_contabilista, cod_ver, cod_fin, dt_ini, dt_fin, ind_perfil) VALUES ( S_cnpj_fornecedor, @idContabilista, S_cod_ver, S_cod_fin, S_dt_ini, S_dt_fin, S_ind_perfil ); SET @idSpedfiscal=(SELECT LAST_INSERT_ID()); ELSE SET @idSpedfiscal=(SELECT id FROM tb_spedfiscal WHERE dt_ini = S_dt_ini AND dt_fin = S_dt_fin AND cnpj_fornecedor = S_cnpj_fornecedor); END IF; END $$
CREATE OR REPLACE PROCEDURE Cadastra_Fornecedor ( IN F_cnpj VARCHAR(17) CHARSET utf8, IN F_nome VARCHAR(255) CHARSET utf8, IN F_xFant VARCHAR(255) CHARSET utf8, IN F_cPais VARCHAR(5) CHARSET utf8, IN F_cMun VARCHAR(7) CHARSET utf8, IN F_uf CHAR(2) CHARSET utf8, IN F_xBairro VARCHAR(60) CHARSET utf8, IN F_xLgr VARCHAR(255) CHARSET utf8, IN F_cep VARCHAR(8) CHARSET utf8, IN F_nro VARCHAR(11) CHARSET utf8, IN F_complemento VARCHAR(60) CHARSET utf8, IN F_fone VARCHAR(16) CHARSET utf8, IN F_fax VARCHAR(16) CHARSET utf8, IN F_email VARCHAR(255) CHARSET utf8, IN F_ie VARCHAR(20) CHARSET utf8, IN F_indIEDest INT, IN F_crt VARCHAR(11) CHARSET utf8, IN F_cpf VARCHAR(11) CHARSET utf8, IN F_im VARCHAR(20) CHARSET utf8, IN F_suframa VARCHAR(9) CHARSET utf8, IN F_ind_ativ INT ) BEGIN IF ( F_cnpj <> "" ) THEN SET @fornecedor = (SELECT id FROM tb_fornecedor WHERE cnpj = F_cnpj); ELSEIF ( F_cpf <> "" ) THEN SET @fornecedor = (SELECT id FROM tb_fornecedor WHERE `cpf` = F_cpf); ELSEIF ( F_ie <> "" ) THEN SET @fornecedor = (SELECT id FROM tb_fornecedor WHERE `ie` = F_ie); END IF; IF @fornecedor IS NULL THEN INSERT INTO tb_fornecedor (cnpj, nome, xFant, cPais, cMun, uf, xBairro, xLgr, cep, nro, complemento, fone, fax, email, ie, indIEDest, crt, cpf, im, suframa, ind_ativ) VALUES ( F_cnpj, F_nome, F_xFant, F_cPais, F_cMun, F_uf, F_xBairro, F_xLgr, F_cep, F_nro, F_complemento, F_fone, F_fax, F_email, F_ie, F_indIEDest, F_crt, F_cpf, F_im, F_suframa, F_ind_ativ ); SET @idFornecedor=(SELECT LAST_INSERT_ID()); ELSE SET @idFornecedor=@fornecedor; END IF; END $$
CREATE OR REPLACE PROCEDURE Cadastra_Participante ( IN P_idSpedfiscal INT, IN P_idFornecedor INT, IN P_codPart VARCHAR(60) CHARSET utf8 ) BEGIN IF (( SELECT COUNT(id) FROM tb_spedfiscal_participante WHERE id_sped = P_idSpedfiscal AND id_fornecedor = P_idFornecedor AND cod_part = P_codPart ) = 0 ) THEN INSERT INTO tb_spedfiscal_participante (id_sped, id_fornecedor, cod_part) VALUES ( P_idSpedfiscal, P_idFornecedor, P_codPart ); SET @idParticipante=(SELECT LAST_INSERT_ID()); ELSE SET @idParticipante=(SELECT id FROM tb_spedfiscal_participante WHERE id_sped = P_idSpedfiscal AND id_fornecedor = P_idFornecedor AND cod_part = P_codPart); END IF; END $$
CREATE OR REPLACE PROCEDURE Cadastra_UnidadesMedidas( IN U_unidade VARCHAR(6) CHARSET utf8, IN U_descricao VARCHAR(100) CHARSET utf8 ) BEGIN SET @idUnidade = (SELECT id FROM tb_unidade_medida WHERE unidade = U_unidade); IF ( @idUnidade IS NULL ) THEN INSERT INTO tb_unidade_medida (unidade, descricao) VALUES ( U_unidade, U_descricao ); SET @idUnidadeMedida=(SELECT LAST_INSERT_ID()); ELSE SET @idUnidadeMedida=(SELECT @idUnidade); END IF; END $$
CREATE OR REPLACE PROCEDURE Cadastra_Produto ( IN P_codItem VARCHAR(60) CHARSET utf8, IN P_descricao VARCHAR(100) CHARSET utf8, IN P_codBarra VARCHAR(50) CHARSET utf8, IN P_codAntItem VARCHAR(6) CHARSET utf8, IN P_unidInv VARCHAR(6) CHARSET utf8, IN P_tipoItem INT(2) UNSIGNED ZEROFILL, IN P_codNcm VARCHAR(8) CHARSET utf8, IN P_exIpi VARCHAR(3) CHARSET utf8, IN P_codGen INT(2), IN P_codLst VARCHAR(5) CHARSET utf8, IN P_aliqICMS DECIMAL(6, 2), IN P_cest INT(7) ) BEGIN IF P_codItem <> "" THEN SET @produto = (SELECT id FROM tb_produto WHERE cod_item = P_codItem); ELSEIF P_descricao <> "" THEN SET @produto = (SELECT id FROM tb_produto WHERE cod_item = P_descricao); END IF; IF @produto IS NULL THEN INSERT INTO tb_produto (cod_item, descricao, cod_barra, cod_ant_item, unidade_inv, tipo_item, cod_ncm, ex_ipi, cod_gen, cod_lst, aliquota_icms, cest) VALUES ( P_codItem, P_descricao, P_codBarra, P_codAntItem, P_unidInv, P_tipoItem, P_codNcm, P_exIpi, P_codGen, P_codLst, P_aliqICMS, P_cest ); SET @idProduto=(SELECT LAST_INSERT_ID()); ELSE SET @idProduto=@produto; END IF; END $$
CREATE OR REPLACE PROCEDURE Cadastra_ProdutoConversao ( IN P_idProduto INT, IN P_unidade VARCHAR(6) CHARSET utf8, IN P_fatorConversao DECIMAL(11, 6) ) BEGIN IF ( (SELECT COUNT(id) FROM tb_produto_conversao WHERE id_produto = P_idProduto AND unidade = P_unidade) = 0 ) THEN INSERT INTO tb_produto_conversao (id_produto, unidade, fator_conversao) VALUES ( P_idProduto, P_unidade, P_fatorConversao ); END IF; END $$
CREATE OR REPLACE PROCEDURE Cadastra_NaturezaOperacao ( IN N_cod VARCHAR(10) CHARSET utf8, IN N_descricao TEXT ) BEGIN IF ( (SELECT COUNT(id) FROM tb_natureza_operacao WHERE cod_nat = N_cod) = 0 ) THEN INSERT INTO tb_natureza_operacao (cod_nat, descr_nat) VALUES ( N_cod, N_descricao ); END IF; END $$
